    &__logo-icon {
      @media (max-width: 700px) {
        display: none;
      }

      &--mobile {
        display: none;

        @media (max-width: 700px) {
          display: block;
        }
      }
    }

    &--light {
      background-color: #fff;
      box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);

      .Header__logo {
        path {
          fill: #000;
        }
      }

      .Header__locale-icon {
        color: #000;
      }
    }

    &--no-logo {
      .Header__logo {
        visibility: hidden;
      }
    }

    &--sticky {
      position: sticky;
    }

    &--full-width {
      .Header__container {
        max-width: 1740px;
      }
    }

    &__menu-icon {
      opacity: 1;
      cursor: pointer;
      transition: opacity .3s;

